#!/usr/bin/perl
use strict;
use warnings;
use FindBin '$Bin';
## ALISAM TECHNOLOGY 2015
####################################################################################################################################################################################################
####################################################################################################################################################################################################
## PRINT BANNER ####################################################################################################################################################################################
our ($nobanner, $output);
if (!defined $nobanner) { banner(); }
